class WidgetMeteo{ajaxRequest(e){$.ajax({url:"http://api.weatherstack.com/current",data:{access_key:"ef1a0c9dadf50406fad67fcc7b5d2f98",query:e},type:"get",dataType:"json",success:function(e){if(0==e.success)switch(e.error.code){case 601:alert("Vous devez saisir le nom d'une ville !");break;case 615:alert("Votre requête a échoué, votre ville est peut être invalide.");break;default:alert(e.error.info)}else $(".meteo").append($("<p> La température à "+e.location.name+" est de "+e.current.temperature+'°C </p><img src="'+e.current.weather_icons+'">'))}})}}