class WidgetNews{ajaxRequest(e){$.ajax({url:"https://newsapi.org/v2/everything?q="+e+"&language=fr&apiKey=9fb4e9e4244f4491bb6310ce8dcd3388",data:"reponse",type:"get",dataType:"json",success:function(a){if(0==a.totalResults)alert("Aucun articles trouvés pour ce sujet");else{$(".articles").remove(),$("#newsheader").append($('<div class="articles"><h3>Top News : '+e+"</h3></div>"));for(var s=0;s<5;s++)$(".articles").append($('<div class="article" id="article'+s+'"></div>')),$("#article"+s).append($('<a href="'+a.articles[s].url+'" target="_blank"></a>')),$("#article"+s+">a").append($('<img class="articleImage" src="'+a.articles[s].urlToImage+'">')),$("#article"+s+">a").append($('<p class="description">'+a.articles[s].description+"</p>"))}},error:function(e){switch(e.status){case 400:alert("Vous n'avez pas saisie de sujet !");break;case 500:alert("Le serveur rencontre actuellement un problème.");default:alert("Un problème est survenu.")}}})}}